package com.yafatek;public class BinarySearchTree {	Node rootNode;	public static void main(String[] args) {		BinarySearchTree bt = new BinarySearchTree();		Node node = new Node(60);		bt.insertNode(node, 60);		bt.inOrder(node);	}	public Node insertNode(Node root, int value) {		if (root == null) root = new Node(value);		if (value < root.Value) root.leftChild = insertNode(root.leftChild, value);		else if (value > root.Value) root.rightChild = insertNode(root.rightChild, value);		return root;	}	private void inOrder(Node node) {		if (node != null) {			inOrder(node.leftChild);			System.out.println(node);			inOrder(node.rightChild);		}	}}