package com.yafatek;import java.util.LinkedList;public class LinkedListsDemo {	public static void main(String[] args) {		LinkedList<String> linkedList = new LinkedList<>();		linkedList.add("feras");		linkedList.add("emad");		linkedList.add("osama");		System.out.println("linked List: " + linkedList);		linkedList.remove("feras");		System.out.println("after remove: " + linkedList);		boolean isExist = linkedList.contains("feras");		System.out.println("is feras Exist?? " + isExist);		System.out.println(linkedList.get(1));	}}